(3, 0, 0, 'lgnLoginTpl', '', 0, 3, 0, '<div class="loginForm">\n    <div class="loginMessage">[[+errors]]</div>\n    <div class="loginLogin">\n        <form class="loginLoginForm" action="/pc" method="post">\n           <ul>\n            <li>\n                <label for="username">[[%login.email]]</label><span class="error">[[+error.username]]</span>\n        				<input type="email" name="username" id="username" value="[[+username]]" />\n            \n            </li>\n            <li>\n                <label for="password">[[%login.password]]</label>\n                <input id="password" type="password" name="password" />\n                <input class="returnUrl" type="hidden" name="returnUrl" value="[[+request_uri]]" />\n                [[+login.recaptcha_html]]\n            </li>\n            <li>\n							<input type="checkbox" name="rememberme" id="login-save" class="checkbox" checked><label for="login-save" class="checkbox">Запомнить меня</label>\n						</li>\n						<li class="button">\n                <input class="loginLoginValue" type="hidden" name="service" value="login" />\n                <span class="loginLoginButton"><input type="submit" name="Login" value="[[+actionMsg]]" /></span>\n            </li>\n          </ul>\n        </form>\n    </div>\n</div>\n<div class="login-form-options">\n			<a href="/pc/password" class="popup bgradius" data-href="pop-password">Вспомнить пароль</a>			\n			<a href="/pc/registration" class="bgradius"><strong>Регистрация</strong></a>			\n		</div>', 0, 'a:0:{}', 0, ''),
(4, 0, 0, 'lgnLogoutTpl', '', 0, 3, 0, '<div class="loginMessage">[[+errors]]</div>\n<br />\n[[+firstname]] [[+lastname]]\n<div class="loginLogin">\n    <div class="loginRegister">\n        <a href="[[+logoutUrl]]" title="[[+actionMsg]]">[[+actionMsg]]</a> \n        &nbsp;\n        <a href="/pc/profile" title="Мой профиль">Мой профиль</a>\n    </div>\n</div>', 0, 'a:0:{}', 0, ''),
