(56, 1, 0, 'tovarOrderItem', '', 0, 2, 0, '<div class="ordervnalichii-item">\n	<div class="ordervnalichii-img" style="background-image:url([[+img]]);"></div>\n	<div class="ordervnalichii-description">\n		<div class="price [[!If? &subject=`[[+tv.pricenew]]` &operator=`!empty` &then=`sale`]]">\n		[[!If? &subject=`[[+tv.price]]` &operator=`!empty` &then=`<span class="price-val">[[formatPrice?price=[[+tv.price]]]]</span>` ]]\n		[[!If? &subject=`[[+tv.pricenew]]` &operator=`!empty` &then=`<span class="price-new">[[formatPrice?price=[[+tv.pricenew]]]]</span>` ]]\n		<span class="rub">руб.</span></div>\n		<div class="text"><a href="[[~[[+id]]]]">[[+pagetitle]]</a></div>\n	</div>\n</div>', 0, 'a:0:{}', 0, ''),
(57, 0, 0, 'orderAddedTpl', '', 0, 11, 0, '<h1>\n	[[+name[[+messageType]]]]\n</h1>\n\n<p><strong>Эл. почта</strong><br>\n[[+email]]</p>\n\n<p><strong>Фамилия</strong><br>\n[[+lastname]]</p>\n\n<p><strong>Имя</strong><br>\n[[+firstname]]</p>\n\n[[+messageType:is=`ordervnalichii`:then=`\n	<p><strong>Товар</strong><br>\n	[[!+tovarid:isnot=``:then=`\n		<a href="[[++site_url]]/[[~[[+tovarid]]]]">[[++site_url]]/[[~[[+tovarid]]]]</a>\n	`]]\n	</p>\n`]]\n[[+messageType:is=`orderkitchen`:then=`\n			<p><strong>Производитель</strong><br> \n				[[!+orderBrand:isnot=``:then=`[[+orderBrand]]	`]]</p>\n			<p><strong>Встраиваемая техника</strong><br> \n				[[!+teh:isnot=``:then=`[[+teh]]	`]]</p>\n			<p><strong>Столешница</strong><br>\n				[[!+worktop:isnot=``:then=`[[+worktop]]	`]]</p>\n		`]]\n\n<p><strong>Описание</strong><br>\n[[+introtext]]</p>\n\n<p><strong>Файлы</strong><br>\n[[!+order-files:isnot=``:then=`\n	[[!formatFiles? &files=`[[+order-files]]`]]\n`]]\n</p>', 0, 'a:0:{}', 0, ''),
