nsion))\r\n{\r\n	$urlExtension = '''';\r\n}\r\n\r\nif ($id == $sitestart) {$output["metax.canonical"] = $siteurl.$urlExtension;}\r\nelse\r\n{\r\n	$urlExtension = '''';\r\n	try\r\n	{\r\n		if($modx->getObject(''modSnippet'', array(''name'' => ''Archivist'')) != NULL)\r\n		{\r\n			/* Support for FURLs created by Archivist: append year and month to canonical url */\r\n			$archivistFilterPrefix = $modx->getOption(''archivistFilterPrefix'',$scriptProperties,''arc_'');\r\n			$archivist = $modx->getService(''archivist'',''Archivist'',$modx->getOption(''archivist.core_path'',null,$modx->getOption(''core_path'').''components/archivist/'').''model/archivist/'',array(''filterPrefix'' => $archivistFilterPrefix));\r\n			if (($archivist instanceof Archivist))\r\n			{\r\n				// Archivist is installed...\r\n				$year = $modx->getOption($archivistFilterPrefix.''year'',$_REQUEST,$modx->getOption(''year'',$scriptProperties,''''));\r\n				$year = (int)$archivist->sanitize($year);\r\n				if(!empty($year)) {$urlExtension = $year;}\r\n				$month = $modx->getOption($archivistFilterPrefix.''month'',$_REQUEST,$modx->getOption(''month'',$scriptProperties,''''));\r\n				$month = (int)$archivist->sanitize($month);\r\n				if (!empty($month))\r\n				{\r\n					if (strlen($month) == 1) $month = ''0''.$month;\r\n					$urlExtension .= ''/''.$month;\r\n				}\r\n			}\r\n		}\r\n	}\r\n	catch (Exception $e) {/*No Archivist, do nothing.*/}\r\n	$output["metax.canonical"] = $modx->makeUrl($id, '''', '''', ''full'').$urlExtension;\r\n}\r\n/*--------------------*/\r\n\r\n/**Favicon**/\r\nif (is_file($favicon)) {$favicon = $siteurl.$favicon;}\r\nelse {$favicon = '''';}\r\n$output["metax.favicon"] = $favicon;\r\n\r\n/**Mobile icon**/\r\nif (is_file($mobile)) {$mobile = $siteurl.$mobile;}\r\nelse {$mobile = '''';}\r\n$output["metax.mobile"] = $mobile;\r\n/*--------------------*/\r\n\r\n/**RSS Feed**/\r\n$rss = explode(",", $rss);\r\n$count = count($rss);\r\n$i = 0;\r\n$output["metax.rss"] = "";\r\nwhile ($i < $count)\r\n{\r\n	$rss[$i] = trim($rss[$i]);\r\n	$tmpdoc = $modx->getObject(''modResource'',array(''id'' => $rss[$i]));\r\n	if ($tmpdoc != NULL)\r\n	{\r\n		$output["metax.rss"] .= "<link href=\\"".$modx->makeUrl($rss[$i], '''', '''', ''full'')."\\" rel=\\"alternate\\" type=\\"application/rss+xml\\" title=\\"".$tmpdoc->get(''pagetitle'')."\\"".$tagend;\r\n	}\r\n	$i++;\r\n	if($i < $count) {$output["metax.rss"] .= "\\n";}\r\n}\r\n/*--------------------*/\r\n\r\n/**CSS Links**/\r\n$css = explode(",", $css);\r\n$count = count($css);\r\n$i = 0;\r\nwhile ($i < $count)\r\n{\r\n	$temp = $css[$i];\r\n	$temp = explode(":", $temp);\r\n	$countt = count($temp);\r\n	$temp[0] = trim($temp[0]);\r\n	if (is_file($temp[0]))\r\n	{\r\n		if ($countt == 1) {$output["metax.css"] .= "<link rel=\\"stylesheet\\" href=\\"".$temp[0]."\\" type=\\"text/css\\"".$tagend;}\r\n		else\r\n		{\r\n			$temp[1] = trim($temp[1]);\r\n			$output["metax.css"] .= "<!--[if ".$temp[1]."]><link rel=\\"stylesheet\\" href=\\"".$temp[0]."\\" type=\\"text/css\\"".$tagend."<![endif]-->";\r\n		}\r\n	}\r\n	$i++;\r\n	if($i < $count) {$output["metax.css"] .= "\\n";}\r\n}\r\n/*--------------------*/\r\n\r\nreturn $modx->getChunk($tpl, $output);', 0, 'a:0:{}', '', 0, ''),
(23, 0, 0, 'Wayfinder', 'Wayfinder for MODx Revolution 2.0.0-beta-5 and later.', 0, 0, 0, '/**\n * Wayfinder Snippet to build site navigation menus\n *\n * Totally refactored from original DropMenu nav builder to make it easier to\n * create custom navigation by using chunks as output templates. By using\n * templates, many of the paramaters are no longer needed for flexible output\n * including tables, unordered- or ordered-lists (ULs or OLs), definition lists\n * (DLs) or in any other format you desire.\n *\n * @version 2.1.1-beta5\n * @author Garry Nutting (collabpad.com)\n * @author Kyle Jaebker (muddydogpaws.com)\n * @author Ryan Thrash (modx.com)\n * @author Shaun McCormick (modx.com)\n * @author Jason Coward (modx.com)\n *\n * @example [[Wayfinder? &startId=`0`]]\n *\n * @var modX $modx\n * @var array $scriptProperties\n * \n * @package wayfinder\n */\n$wayfinder_base = $modx->getOption(''wayfinder.core_path'',$scriptProperties,$modx->getOption(''core_path'').''components/wayfinder/'');\n\n/* include a custom config file if specified */\nif (isset($scriptProperties[''config''])) {\n    $scriptProperties[''config''] = str_replace(''../'','''',$scriptProperties[''config'']);\n    $scriptProperties[''config''] = $wayfinder_base.''configs/''.$scriptProperties[''config''].''.config.php'';\n} else {\n    $scriptProperties[''config''] = $wayfinder_base.''configs/default.config.php'';\n}\nif (file_exists($scriptProperties[''config''])) {\n    include $scriptProperties[''config''];\n}\n\n/* include wayfinder class */\ninclude_once $wayfinder_base.''wayfinder.class.php'';\nif (!$modx->loadClass(''Wayfinder'',$wayfinder_base,true,true)) {\n    return ''error: Wayfinder class not found'';\n}\n$wf = new Wayfinder($modx,$scriptProperties);\n\n/* get user class definitions\n * TODO: eventually move these into config parameters */\n$wf->_css = array(\n    ''first'' => isset($firstClass) ? $firstClass : '''',\n    ''last'' => isset($lastClass) ? $lastClass : ''last'',\n    ''